import React, { useContext, useEffect, useState } from "react";
import { CanvasContext } from "../../contexts/CanvasContext";
import Layout from "../../components/templates/Layout/Layout";
import { TextField, Button, Avatar } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Prompt } from "react-router-dom";
import { useRouter } from "next/router";

export async function getStaticPaths() {
  return { paths: [], fallback: false };
}

export async function getStaticProps({ params }) {
  console.log("params", params);
}

const Canvas = (props) => {
  const classes = useStyles();
  const { enterCanvas, joinedUsers } = useContext(CanvasContext);
  // = props.location.state.canvasId;
  const router = useRouter();
  const { canvasId } = router.query;
  console.log(canvasId);

  useEffect(() => {
    enterCanvas(canvasId);
  }, []);
  const users = joinedUsers.map((user) => (
    <Avatar user={user} key={user.id} alt="user" src={user.image_url} />
  ));

  return (
    <Layout>
      <Prompt message={"canvasから退出しますか？"} />
      <div className={classes.avatars}>{users}</div>
      <TextField></TextField>
      <Button type="submit" fullWidth variant="contained" color="primary">
        送信
      </Button>
    </Layout>
  );
};

export default Canvas;

///////
//Style
///////
const useStyles = makeStyles({
  avatars: {
    display: "flex",
  },
});
