import React, { useContext, useEffect, useState } from "react";
import { CanvasContext } from "../../contexts/CanvasContext";
import Layout from "../../components/templates/Layout/Layout";
import { TextField, Button, Avatar } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useRouter } from "next/router";
import firebase from "../../firebase/firebase";

export async function getCanvasIds() {
  const db = firebase.firestore();
  const canvases = [];
  return await new Promise(async (resolve, reject) => {
    try {
      const canvasesRef = await db.collection("canvases");
      await canvasesRef.onSnapshot((snapshot) => {
        snapshot.docs.forEach((doc) => {
          console.log(doc.id);
          canvases.push({ params: { id: doc.id } });
        });
      });
      return resolve(canvases);
    } catch (error) {
      return reject([]);
    }
  });
}

export async function getStaticPaths() {
  const ids = await getCanvasIds();
  console.log("ids", ids);
  return {
    paths: ["/Canvases/GIQH22GnoC9IHrkkTs82"],
    fallback: false,
  };
}

