import React, { useContext } from "react";
import { CanvasContext } from "../contexts/CanvasContext";
import Layout from "../components/templates/Layout/Layout";
import CreateCanvas from "../components/organisms/CreateCanvas";
import SelectCanvas from "../components/organisms/SelectCanvas";
import { db } from "../firebase/firebase";

export async function getServerSideProps() {
  try {
    const canvases = [];
    const canvasesRef = await db.collection("canvases");
    canvasesRef.onSnapshot((snapshot) => {
      snapshot.docChanges().forEach((change) => {
        canvases.push({
          id: change.doc.id,
          name: change.doc.data().name,
          created_by: change.doc.data().created_by,
          created_at: change.doc.data().created_at,
        });
        // 追加
        if (change.type === "added") {
          // addLog(change.doc.id, change.doc.data());
        }
        // 更新
        else if (change.type === "modified") {
          // modLog(change.doc.id, change.doc.data());
        }
        // 削除
        else if (change.type === "removed") {
          // removeLog(change.doc.id);
        }
      });
    });
    return {
      props: {
        canvases,
      },
    };
  } catch (error) {
    console.log(error);
  }
}

const CanvasListPage = ({ canvases }) => {
  return (
    <Layout>
      <CreateCanvas />
      <div>Canvas一覧</div>
      <SelectCanvas canvases={canvases} />
    </Layout>
  );
};

export default CanvasListPage;
